mapper.select:
SELECT BOARD_TYPE ,BOARD_NUM ,BOARD_TITLE ,BOARD_COMMENT ,TOTAL_CNT FROM ( SELECT BOARD_TYPE ,BOARD_NUM ,BOARD_TITLE ,BOARD_COMMENT ,ROW_NUMBER() OVER(ORDER BY BOARD_NUM DESC) AS NUMROW ,COUNT(*) OVER() AS TOTAL_CNT FROM BOARD
WHERE BOARD_TYPE IN
#{arr}
) WHERE NUMROW BETWEEN (#{pageNo}-1)*10 + 1 AND (#{pageNo})*10 ORDER BY BOARD_TYPE, BOARD_NUM desc

mapper.delete:
delete 
from board 
where BOARD_TYPE = #{boardType} 
and BOARD_NUM = #{boardNum}

mapper.update:
update board 
set BOARD_TITLE = #{boardTitle}
	, BOARD_COMMENT = #{boardComment} 
where BOARD_TYPE = #{boardType} 
and BOARD_NUM = #{boardNum}

mapper.select:
SELECT CODE_TYPE, CODE_ID, CODE_NAME
FROM COM_CODE
WHERE CODE_TYPE = #{codeType}
